package arraysInJava;
public class Array3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] num= {10,20,30};
		int sum=0;
		for(int i=0;i<num.length; i++) {
			sum=sum+num[i];
			//sum=sum/num.length;
		//System.out.println("sum of array is"+sum);
		
		}
		System.out.println("sum of array is "+sum);
		//you can also find avarage of numbers
		//you can also find avarage of numbers
		System.out.println("avarage of array is "+sum/num.length);
		
		

	}

}
